// Imports
// ========================================================
import { useState } from "react";
import { Pressable, Text } from "react-native";
import { useAccount, useWaitForTransaction } from "wagmi";
import { encodeAbiParameters } from "viem";
import { openURL } from "expo-linking";

// Constants
// ========================================================
/**
 * @dev ByteCode for HelloWorld Contract see https://github.com/berachain/guides/blob/main/apps/hardhat-viem-helloworld/contracts/HelloWorld.sol
 */
const CONTRACT_BYTECODE =
  "";


// Component
// ========================================================
export default function Deploy() {
  // Hooks
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [transactionHash, setTransactionHash] = useState<`0x${string}` | undefined>();
  const { isConnected, address, connector } = useAccount();

  // Functions
  /**
   * @dev hook that waits for a transaction hash
   */
  const txResult = useWaitForTransaction({
    hash: transactionHash
  });

  /**
   * 
   */
  const onPressDeployContract = async () => {
    console.group('onPressDeployContract');
    setError('');
    setIsLoading(true);
    try {
      const provider = await connector?.getProvider();
      console.log({ provider });
      console.log({ request: provider?.request });

      // Based on constructor - constructor(string memory _greeting) {
      const encodedData = encodeAbiParameters(
        [{ name: "_greeting", type: "string" }],
        ["Hello World!"]
      );

      // Need slide(2) to remove 0x from encodedData at the beginning
      const fullByteCode = `${CONTRACT_BYTECODE}${encodedData.slice(2)}`;

      const tx = await provider.request({
        method: "eth_sendTransaction",
        params: [
          {
            from: address,
            data: fullByteCode,
          },
        ],
      });
      console.log({ tx });
      setTransactionHash(tx);
    } catch (error: any) {
      console.error(error?.reason);
      console.error(error);
      setError('Error could not deploy contract.')
    }
    setIsLoading(false);

    console.groupEnd();
  };

  /**
   * @dev function that handles opening url to the transaction hash in a block explorer
   */
  const onPressSeeTransaction = () => {
    openURL(`https://artio.beratrail.io/tx/${txResult?.data?.transactionHash}`);
  };

  // Return
  if (!isConnected || !address) return null;

  return (
    <>
      <Text className="text-[#2E1E1A] mb-2">Deploy Contract</Text>
      <Pressable
        disabled={isLoading}
        className={`bg-[#2E1E1A] h-12 flex items-center justify-center rounded-lg mb-4 ${isLoading ? 'opacity-20' : ''}`}
        onPress={onPressDeployContract}>
        <Text className="text-white text-base">
          Deploy
        </Text>
      </Pressable>

      <Text className="text-white text-base">
        {/* {JSON.stringify({ txResult: (txResult as any)?.data })} */}
      </Text>

      {isLoading && <Text className="bg-[#ff843d] whitespace-nowrap overflow-scroll mb-4 text-[#874c2a] placeholder:text-[#874c2a] text-base leading-10 px-2 w-full">Loading...</Text>}

      {!isLoading && txResult?.data?.transactionHash
        ? <Pressable
          className="bg-[#2E1E1A] h-12 flex items-center justify-center rounded-lg mb-4"
          onPress={onPressSeeTransaction}>
          <Text className="text-white text-base">
            See Successful Transaction
          </Text>
        </Pressable>
        : null}
      {error ? <Text className="text-red-800 bg-red-200 mb-2 text-base p-4">{error}</Text> : null}
    </>
  );
}