"use client";

// Imports
// ========================================================
import { useAccount, useWaitForTransactionReceipt } from "wagmi";
import ClientOnly from "../ClientOnly";
import { useState } from "react";
import { encodeAbiParameters } from "viem";
import { berachainTestnet } from "wagmi/chains";

// Constants
// ========================================================
/**
 * @dev All inputs and outputs of Contract
 */
const CONTRACT_ABI = [
  {
    "inputs": [
      {
        "internalType": "string",
        "name": "_greeting",
        "type": "string"
      }
    ],
    "stateMutability": "nonpayable",
    "type": "constructor"
  },
  {
    "anonymous": false,
    "inputs": [
      {
        "indexed": false,
        "internalType": "address",
        "name": "sender",
        "type": "address"
      },
      {
        "indexed": false,
        "internalType": "string",
        "name": "message",
        "type": "string"
      }
    ],
    "name": "NewGreeting",
    "type": "event"
  },
  {
    "inputs": [],
    "name": "getGreeting",
    "outputs": [
      {
        "internalType": "string",
        "name": "",
        "type": "string"
      }
    ],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [
      {
        "internalType": "string",
        "name": "_greeting",
        "type": "string"
      }
    ],
    "name": "setGreeting",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  }
];

/**
 * @dev full contract bytecode for deployment
 */
const CONTRACT_BYTECODE = '';

/**
 * @dev Berachain testnet block explorer
 */
const BLOCK_EXPLORER = 'https://artio.beratrail.io/';

// Main Page
// ========================================================
export default function Deploy() {
  // Hooks
  const { address, isConnected, connector } = useAccount();
  const [transactionHash, setTransactionHash] = useState('');
  const [greeting, setGreeting] = useState('');
  const [isLoading, setIsloading] = useState(false);
  const [error, setError] = useState('');

  const receipt = useWaitForTransactionReceipt({
    chainId: berachainTestnet.id,
    hash: transactionHash ? `${transactionHash}` as `0x${string}` : undefined,
  });

  // Functions
  /**
   * @dev function that handles sending a transaction to the blockchain
   */
  const deployContract = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    setTransactionHash('');
    setError('');

    console.group("deployContract");
    setIsloading(true);
    try {

      // Why not use? usePrepareTransactionRequest or useSendTransaction
      // Not enough documentation from wagmi on how to do a simple `eth_sendTransaction` with bytecode and not provide `to` param
      const provider = await connector?.getProvider() as any;
      console.log({ provider });
      console.log({ request: provider?.request });
      console.log({ greeting });

      // Based on constructor - constructor(string memory _greeting) {
      const encodedData = encodeAbiParameters(
        [{ name: "_greeting", type: "string" }],
        [`${greeting}`]
      );

      // Need slide(2) to remove 0x from encodedData at the beginning
      const fullByteCode = `${CONTRACT_BYTECODE}${encodedData.slice(2)}` as `0x${string}`;

      // Process trasaction
      const tx = await provider.request({
        method: "eth_sendTransaction",
        params: [
          {
            from: address,
            data: fullByteCode,
          },
        ],
      });

      // Get the transaction hash
      setTransactionHash(tx);

      // Refetch wait for receipt
      receipt.refetch();
    } catch (error: any) {
      console.error(error?.message);
      console.error(error?.reason);
      console.error(error);
      setError(error?.message);
    }
    setIsloading(false);
    console.groupEnd();
  };

  // Render
  return (
    <ClientOnly>
      <section className="pb-6 mb-6 border-zinc-700 border-b">
        <>
          <h2>Deploy Contract</h2>
          {isConnected
            ? <div>
              <form onSubmit={deployContract}>
                <div>
                  <label>HelloWorld.sol</label>
                  <pre><code>{JSON.stringify(CONTRACT_ABI, null, '   ')}</code></pre>
                </div>
                <div>
                  <label htmlFor="_greeting">_greeting</label>
                  <input disabled={isLoading} placeholder="Ex: Hello There!" type="text" name="_greeting" id="_greetine" value={greeting} onChange={(e) => setGreeting(e.target.value)} />
                </div>
                <div>
                  <label>ByteCode</label>
                  <pre><code>{CONTRACT_BYTECODE}</code></pre>
                </div>
                <div>
                  <button disabled={isLoading || greeting.length === 0} type="submit">Deploy</button>
                </div>
              </form>

              <div>
                <label>Transaction Hash Result</label>
                <pre><code>{transactionHash}{error}</code></pre>
                {transactionHash ? <p><a className="button" href={`${BLOCK_EXPLORER}/tx/${transactionHash}`} target="_blank">Beratrail Tx Link</a></p> : null}
              </div>

              <div>
                <label>Transaction Receipt</label>
                <pre><code>{receipt.status === 'pending' ? `Status: ${receipt.status}\n\nWaiting...` : ''}{receipt.status === 'error' ? `Status: ${receipt.status}\n\n${receipt?.failureReason?.message}` : ''}{receipt.status === 'success' ? `Status: ${receipt.status}\n\n${receipt?.data?.contractAddress}` : ''}</code></pre>

                {receipt?.data?.contractAddress ? <p><a className="button" href={`${BLOCK_EXPLORER}/address/${receipt?.data?.contractAddress}`} target="_blank">Beratrail Contract Address Link</a></p> : null}
              </div>
            </div>
            : <div>
              <pre><code>Not Connected</code></pre>
            </div>
          }
        </>
      </section>
    </ClientOnly>
  )
};
